-- 1. Indique la cantidad de productos que tiene la empresa.SELECT COUNT(Id_producto) as Cantidad_De_Productos
FROM Producto;

-- 2. Indique la cantidad de productos en estado 'en stock' que tiene la empresa.SELECT COUNT(*) as En_StockFROM ProductoWHERE estado='stock';-- 3. Indique los productos que nunca fueron vendidos.SELECT Id_producto, nombre, Descripcion, estado, Id_proveedorFROM ProductoWHERE Id_producto NOT IN (SELECT Id_producto FROM Detalle_venta);-- 4. Indique la cantidad de unidades que fueron vendidas de cada productoSELECT Id_producto, COUNT(Id_producto) as VentasFROM Detalle_ventaGROUP BY Id_producto;-- 5. Indique cual es la cantidad promedio de unidades vendidas de cada producto.SELECT Id_producto, AVG(Cantidad) as VentasFROM Detalle_ventaGROUP BY Id_producto;-- 6. Indique quien es el vendedor con mas ventas realizadas.SELECT Id_vendedorFROM VentaGROUP BY Id_vendedorHAVING MAX(Id_vendedor) = COUNT(Id_vendedor);-- 7. Indique todos los productos de lo que se hayan vendido más de 15.000 unidadesSELECT Id_productoFROM Detalle_ventaGROUP BY Id_productoHAVING SUM(Cantidad) > 15000;-- 8. Indique quien es el vendedor con mayor volumen de ventas.SELECT Id_vendedorFROM Venta VINNER JOIN Detalle_venta DVON V.Nro_factura = DV.Nro_facturaGROUP BY Id_vendedorHAVING SUM(Cantidad) >= ALL(SELECT SUM(CANTIDAD) 							FROM Venta V							INNER JOIN Detalle_venta DV							ON V.Nro_factura = DV.Nro_factura							GROUP BY Id_vendedor 							);